(5419, '017. Explozie de furie! Kuro contra Luffy! Cum se termină!', '2023-08-23', 'one-piece', 'one-piece-s1', 'One Piece', 'Atacul final al lui Kuro distruge totul în zonă, iar Luffy se înfurie din cauza lipsei sale de respect pentru echipajul său.', '<div style=\"position:relative;padding-top:56.25%;\"><iframe src=\"https://iframe.mediadelivery.net/embed/149768/9b4ccb3c-c524-4b75-ada7-a9abcecad271?autoplay=false&loop=false&muted=false&preload=true\" loading=\"lazy\" style=\"border:none;position:absolute;top:0;height:100%;width:100%;\" allow=\"accelerometer;gyroscope;autoplay;encrypted-media;picture-in-picture;\" allowfullscreen=\"true\"></iframe></div>'),
(5420, '018. Ești o creatura ciudată! Gaimon și prietenii săi ciudați!', '2023-08-23', 'one-piece', 'one-piece-s1', 'One Piece', 'undefined', '<div style=\"position: relative; padding-top: 56.25%;\"><iframe src=\"https://iframe.mediadelivery.net/embed/149768/63ace5e5-09cb-4e2f-9174-b414554439a8?autoplay=false&preload=false\" loading=\"lazy\" style=\"border: none; position: absolute; top: 0; height: 100%; width: 100%;\" allow=\"accelerometer; gyroscope; autoplay; encrypted-media; picture-in-picture;\" allowfullscreen=\"true\"></iframe></div>');
